mapper.update:
UPDATE financial_record
SET redeem_time   = now(),
    update_time   = now(),
    hold_amount   = hold_amount - #{amount},
    income_amount = income_amount - #{amount}
WHERE id = #{recordId}
  AND hold_amount = #{originalHoldAmount}
  AND `status` = 'PROCESS'

UPDATE financial_record
SET redeem_time   = now(),
    update_time   = now(),
    hold_amount   = hold_amount - (#{incomeAmount} + #{waitAmount}),
    income_amount = income_amount - #{incomeAmount},
    wait_amount   = wait_amount - #{waitAmount}
WHERE id = #{recordId}
  AND hold_amount = #{originalHoldAmount}
  AND `status` = 'PROCESS'

UPDATE financial_record
SET hold_amount = hold_amount + #{amount},
    update_time = #{now}
WHERE id = #{recordId}
  AND hold_amount = #{originalAmount}
  AND `status` = 'PROCESS'

UPDATE financial_record
SET wait_amount = wait_amount + #{amount},
    hold_amount = hold_amount + #{amount},
    update_time = #{now}
WHERE id = #{recordId}
  AND wait_amount = #{originalAmount}
  AND `status` = 'PROCESS'

UPDATE financial_record
SET income_amount = income_amount + #{amount},
    hold_amount   = hold_amount + #{amount},
    wait_amount   = 0,
    update_time   = #{now}
WHERE id = #{recordId}
  AND income_amount = #{originalAmount}
  AND `status` = 'PROCESS'

UPDATE financial_record
SET rate = #{rate}
WHERE product_id = #{productId}
  AND `status` = 'PROCESS'

mapper.select:
SELECT hold_amount as amount, coin FROM financial_record
product_type = #{productType}
AND status = 'PROCESS'

SELECT product_id , sum(hold_amount) as use_quota, count(distinct uid) as hold_user_count FROM financial_record WHERE product_id in
#{item}
AND status = 'PROCESS' GROUP BY product_id
