mapper.select:
SELECT temp.*, pro.`name`, pro.name_en, pro.coin, pro.logo, pro.type as productType FROM (
SELECT id as record_id, product_id, hold_amount, create_time AS time FROM fund_record WHERE `status` = 'PROCESS' AND uid = #{uid}
UNION
SELECT id as record_id, product_id, hold_amount, purchase_time AS time FROM financial_record WHERE `status` = 'PROCESS' AND uid = #{uid}
AND product_type = #{type}
) temp LEFT JOIN financial_product pro ON temp.product_id = pro.id ORDER BY time DESC

select temp.*,pro.type as product_type,pro.logo,pro.name,pro.name_en from (select o.order_no as details_id,o.amount,o.coin, o.create_time, o.type, o.`status`, oa.product_id as product_id from `order` o left join order_advance oa on o.related_id = oa.id where o.type in ('purchase', 'redeem','transfer') AND o.uid = #{uid} and o.order_no like 'APU%' UNION select o.order_no as details_id, o.amount,o.coin, o.create_time, o.type, o.`status`, fe.product_id as product_id from `order` o left join financial_record fe on o.related_id = fe.id where o.type in ('purchase', 'redeem','transfer') AND o.uid = #{uid} and o.order_no not like 'APU%'
union select id as details_id, transaction_amount as amount , coin, create_time, CASE type WHEN 'redemption' THEN 'redemption' ELSE type END, CASE `status` WHEN 1 THEN 'chain_success' WHEN 2 THEN 'chaining' WHEN 3 THEN 'created' WHEN 4 and type = 'redemption' THEN 'review_fail' ELSE 'chain_fail' END as `status`, product_id from fund_transaction_record where uid = #{uid}
) temp inner join financial_product pro on temp.product_id = pro.id where 1=1
and pro.type = #{type}
ORDER BY temp.create_time desc
