mapper.select:
        SELECT
            ifnull(SUM(borrow_capital),0.0)
        FROM borrow_coin_order
        
            
                borrow_time >= #{startTime}
            
            
                and borrow_time <= #{endTime}

SELECT
    ifnull(count(*),0)
FROM borrow_coin_order

    status = #{status}
    
        and borrow_time >= #{startTime}
    
    
     and borrow_time <= #{endTime}

select
    ifnull(SUM(cumulative_interest),0.0)
from borrow_coin_order

    
        uid like concat('%',#{queryUid},'%')
    
    
        and id like concat('%',#{queryOrderId},'%')
    
    
        and pledge_rete >= #{minPledgeRate}
    
    
        and pledge_rete <= #{maxPledgeRate}
    
    
        and borrow_time >= #{startTime}
    
    
        and borrow_time <= #{endTime}
    
    
        and status = #{status}
