mapper.select:
        SELECT ifnull(SUM(interest_accrual),0)
        FROM borrow_interest_record
        
            
                order_id = #{orderId}
            
            
                and interest_accrual_time >= #{startTime}
            
            
                and interest_accrual_time <= #{endTime}

