mapper.select:
select
    sum(interest_amount) amount,
    coin
from fund_income_record


        uid = #{uid}


        and uid like concat('%',#{queryUid},'%')


        and product_id like concat('%',#{queryProductId},'%')


        and status = #{status}


        and create_time >= #{startTime}


        and create_time <= #{endTime}


group by coin
