mapper.select:
SELECT sum(a.hold_amount) amount,a.coin FROM wallet_agent_product b inner JOIN fund_record a on a.product_id = b.product_id
b.agent_id = #{query.agentId}
and a.product_id = #{query.productId}
group by a.coin

SELECT ifnull(sum(hold_amount),0)
FROM fund_record


        product_id = #{productId}


        and uid = #{uid}

SELECT count(DISTINCT a.uid)

FROM wallet_agent_product b
INNER JOIN fund_record a on a.product_id = b.product_id

    a.status = 'PROCESS'

        and b.agent_id = #{query.agentId}


        and b.product_id = #{query.productId}

select coin, SUM(hold_amount) as holdAmount, sum(cumulative_income_amount) interestAmount, SUM(wait_income_amount) as waitInterestAmount, SUM(income_amount) as payInterestAmount from fund_record a where product_id in ( select product_id from wallet_agent_product
and agent_id = #{agentId}
) and `status` = 'PROCESS'
and uid like concat('%',#{queryUid},'%')
GROUP BY coin

select sum(r.hold_amount) amount, r.coin from fund_record r left join wallet_agent_product p on r.product_id=p.product_id where r.uid = #{uid}
and p.agent_id = #{agentId}
r.status = 'PROCESS' group by r.coin
