mapper.select:
select
    sum(transaction_amount) amount,
    coin
from
    fund_transaction_record


        uid like concat('%',#{queryUid},'%')


        fund_id like concat('%',#{queryFundId},'%')


        product_id like concat('%',#{queryProductId},'%')


        type = #{type}


        status = #{status}


        create_time >= #{startTime}


        create_time <= #{endTime}


group by coin
