mapper.select:
select
    sum(if(type = 'purchase',a.transaction_amount,0 ) ) purchaseAmount,
    sum(if(type = 'redemption',a.transaction_amount,0 ) ) redemptionAmount,
    a.coin
from
    fund_transaction_record a
    left join wallet_agent_product b on a.product_id = b.product_id


        and a.uid like concat('%',#{queryUid},'%')


        and a.fund_id like concat('%',#{queryFundId},'%')


        and a.product_id like concat('%',#{queryProductId},'%')


        and a.type = #{type}


        and a.status = #{status}


        and a.create_time >= #{startTime}


        and a.create_time <= #{endTime}


        and b.uid = #{agentUId}


group by coin

select a.id, a.uid uid, a.product_id productId, a.product_name productName, c.product_name_en productNameEn, a.coin, c.rate, a.transaction_amount transactionAmount, a.type, a.status, a.create_time createTime from fund_transaction_record a left join wallet_agent_product b on a.product_id = b.product_id left join fund_record c on a.fund_id = c.id
and a.uid like concat('%',#{query.queryUid},'%')
and a.fund_id like concat('%',#{query.queryFundId},'%')
and a.product_id like concat('%',#{query.queryProductId},'%')
and a.type = #{query.type}
and a.status = #{query.status}
and a.create_time >= #{query.startTime}
and a.create_time <= #{query.endTime}
and b.uid = #{query.agentUId}
order by a.create_time desc
