mapper.select:
select sum(if(type = 'purchase',a.transaction_amount,0 ) ) purchaseAmount, sum(if(type = 'redemption',a.transaction_amount,0 ) ) redemptionAmount, a.coin from fund_transaction_record a left join wallet_agent_product b on a.product_id = b.priduct_id
and a.uid like concat('%',#{queryUid},'%')
and a.fund_id like concat('%',#{queryFundId},'%')
and a.product_id like concat('%',#{queryProductId},'%')
and a.type = #{type}
and a.status = #{status}
and a.create_time >= #{startTime}
and a.create_time <= #{endTime}
and b.agent_id = #{agentId}
b.deleted = 0
group by coin

select a.id, a.uid uid, a.product_id productId, a.product_name productName, a.coin, b.rate, a.transaction_amount transactionAmount, a.type, a.status, a.create_time createTime from fund_transaction_record a left join wallet_agent_product b on a.product_id = b.priduct_id left join fund_record c on a.fund_id = b.id
and a.uid like concat('%',#{query.queryUid},'%')
and a.fund_id like concat('%',#{query.queryFundId},'%')
and a.product_id like concat('%',#{query.queryProductId},'%')
and a.type = #{query.type}
and a.status = #{query.status}
and a.create_time >= #{query.startTime}
and a.create_time <= #{query.endTime}
and b.agent_id = #{query.agentId}
b.deleted = 0
order by a.create_time desc
